mapper.select:
SELECT gus.login, gus.name, gus.avatar_url As avatarUrl, gusc.country_iso, gus.total_score AS totalScore, gus.user_score AS userScore, gus.repos_score AS reposScore, gus.prs_score AS prsScore, gus.issues_score AS issuesScore FROM t_github_user_score gus LEFT JOIN t_github_user_country_guess gusc ON gus.login = gusc.login
AND gusc.country_iso = #{nation}
ORDER BY totalScore DESC LIMIT #{size}

SELECT temp.login, temp.name, temp.avatarUrl, temp.country_iso, temp.totalScore, temp.userScore, temp.reposScore, temp.prsScore, temp.issuesScore FROM ( SELECT gus.login, gusc.country_iso, gus.name, gus.avatar_url As avatarUrl, gus.total_score AS totalScore, gus.user_score AS userScore, gus.repos_score AS reposScore, gus.prs_score AS prsScore, gus.issues_score AS issuesScore FROM t_github_user_score gus LEFT JOIN t_github_user_country_guess gusc ON gus.login = gusc.login
AND gusc.country_iso = #{nation}
) temp LEFT JOIN t_github_user_developer_type gudt ON temp.login = gudt.login
AND gudt.developer_type = #{type}
ORDER BY temp.totalScore DESC LIMIT #{size}
